mapper.select:
select item_id, paper_id, quest_id, score, quest_type, answer_time, status, crt_user_code, 
crt_org_code, crt_date, upd_user_code, upd_org_code, upd_date
from resource_paper_items


    and paper_id = #{paperId}


    and quest_id = #{questId}


    and score = #{score}


    and quest_type = #{questType}


    and answer_time = #{answerTime}


    and status = #{status}


    and crt_user_code = #{crtUserCode}


    and crt_org_code = #{crtOrgCode}


    and crt_date = #{crtDate}


    and upd_user_code = #{updUserCode}


    and upd_org_code = #{updOrgCode}


    and upd_date = #{updDate}

mapper.delete:
delete from resource_paper_items
where item_id in 

  ${id}
